import { useState } from "react";
import CardInfo from "./CardInfo";
import PropTypes from "prop-types";

const UserInfo = ({ className = "" }) => {
  const [name, setName] = useState("Vince Albert Juson");
  const [email, setEmail] = useState("vincejuson@ust.edu.ph");
  const [contact, setContact] = useState("09951778731");

  const handleSaveProfile = (newText) => {
    // Update the relevant user information based on the edited field
    setName(newText); // Here, we're assuming the user is editing the name
    // You can add more logic to determine which field is being edited (e.g., email, contact)
  };

  return (
    <div
      className={`self-stretch bg-[#fff] flex flex-col items-center justify-center pt-[25px] px-[101px] pb-[23px] box-border gap-[25px] max-w-full z-[1] mt-[-23px] text-center text-[24px] text-[#4b4b4b] font-[Poppins] mq750:pl-[50px] mq750:pr-[50px] mq750:box-border mq450:pl-[20px] mq450:pr-[20px] mq450:pb-[20px] mq450:box-border ${className}`}
    >
      <div className="w-[566px] h-[394px] relative bg-[#fff] hidden max-w-full" />
      <div className="w-[334.5px] flex flex-row items-center justify-center py-[0px] px-[54px] box-border max-w-full text-left mq450:pl-[20px] mq450:pr-[20px] mq450:box-border">
        <div className="flex-1 flex flex-col items-center justify-center">
          <div className="flex flex-row items-center justify-center py-[0px] pl-[6px] pr-[7px]">
            <h3 className="m-[0px] h-[65px] relative text-inherit tracking-[0.2px] leading-[24px] font-bold font-[inherit] flex items-center z-[2] mq450:text-[19px] mq450:leading-[19px]">
              {name} {/* Dynamically display the updated name */}
            </h3>
          </div>
          <div className="self-stretch h-[37px] relative text-[20px] font-[Inter] text-[#787486] text-center inline-block shrink-0 z-[3] mt-[-13px] mq450:text-[16px]">
            Student
          </div>
        </div>
      </div>
      <div className="w-[351.5px] flex flex-row items-center justify-center pt-[0px] px-[0px] pb-[4px] box-border max-w-full">
        <h3 className="m-[0px] h-[65px] flex-1 relative text-inherit tracking-[0.2px] leading-[24px] flex items-center justify-center max-w-full z-[2] font-[inherit] mq450:text-[19px] mq450:leading-[19px]">
          <span className="[line-break:anywhere]">
            <p className="m-[0px]">
              <b>Email</b>
            </p>
            <p className="m-[0px]">{email}</p> {/* Dynamically display the updated email */}
          </span>
        </h3>
      </div>
      <div className="w-[349.5px] flex flex-row items-center justify-center pt-[0px] px-[69px] pb-[15px] box-border max-w-full mq450:pl-[20px] mq450:pr-[20px] mq450:box-border">
        <div className="flex-1 flex flex-row items-center justify-center relative">
          <div className="h-[65px] flex-1 relative tracking-[0.2px] leading-[24px] flex items-center justify-center z-[2] mq450:text-[19px] mq450:leading-[19px]">
            <span className="[line-break:anywhere]">
              <p className="m-[0px]">
                <b>Contact</b>
              </p>
              <p className="m-[0px]">{contact}</p> {/* Dynamically display the updated contact */}
            </span>
          </div>
        </div>
      </div>
      <div className="self-stretch flex flex-row items-center justify-center py-[0px] pl-[1px] pr-[0px]">
        <CardInfo onSave={handleSaveProfile} />
      </div>
    </div>
  );
};

UserInfo.propTypes = {
  className: PropTypes.string,
};

export default UserInfo;
