import React, { useState, useCallback, useEffect } from 'react';
import axios from 'axios';  
import './assetlist.css';
import AssetSearchbar from "./components/assetsearchbar";
import AssetTable from "./components/assettable";
import InfoCards from "./components/infocards";
import AddAsset from "./components/addasset";
import AssetCategory from "./components/addcategory";
import AssetLocation from "./components/assetlocation";
import SortDropdown from "./components/sortdropdown";

const AssetList = () => {
  const [assets, setAssets] = useState([]);
  const [categories, setCategories] = useState([]);
  const [locations, setLocations] = useState([]);
  const [assetID, setAssetID] = useState(1);
  const [assetToEdit, setAssetToEdit] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [activeAssetIDs, setActiveAssetIDs] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [sortCriteria, setSortCriteria] = useState("");

  // Fetch initial data from backend
  useEffect(() => {
    const fetchData = async () => {
      try {
        const assetRes = await axios.get('/api/assets');
        setAssets(assetRes.data);

        const categoryRes = await axios.get('/api/categories');
        setCategories(categoryRes.data);

        const locationRes = await axios.get('/api/locations');
        setLocations(locationRes.data);
      } catch (err) {
        console.error('Error fetching data:', err);
      }
    };

    fetchData();
  }, []);

  const handleAddAsset = useCallback(async (newAsset) => {
    try {
      if (assetToEdit) {
        const updatedAsset = await axios.put(`/api/assets/${assetToEdit._id}`, newAsset);
        setAssets((prevAssets) => prevAssets.map(asset => asset._id === assetToEdit._id ? updatedAsset.data : asset));
      } else {
        const createdAsset = await axios.post('/api/assets', newAsset);
        setAssets((prevAssets) => [...prevAssets, createdAsset.data]);
      }
      setAssetToEdit(null);
      setIsModalOpen(false);
    } catch (err) {
      console.error('Error adding/updating asset:', err);
    }
  }, [assetToEdit]);

  const handleDeleteAsset = useCallback(async (id) => {
    try {
      await axios.delete(`/api/assets/${id}`);
      setAssets((prevAssets) => prevAssets.filter(asset => asset._id !== id));
      setActiveAssetIDs((prevActiveIDs) => prevActiveIDs.filter(assetID => assetID !== id));
    } catch (err) {
      console.error('Error deleting asset:', err);
    }
  }, []);

  const handleAddCategory = useCallback(async (newCategoryData) => {
    try {
      const createdCategory = await axios.post('/api/categories', newCategoryData);
      setCategories((prev) => [...prev, createdCategory.data]);
    } catch (err) {
      console.error('Error adding category:', err);
    }
  }, []);

  const handleDeleteCategory = useCallback(async (categoryToDelete) => {
    try {
      await axios.delete(`/api/categories/${categoryToDelete}`);
      setCategories((prevCategories) => prevCategories.filter(category => category.category !== categoryToDelete));
      setAssets((prevAssets) => prevAssets.map(asset => {
        if (asset.selectedCategory === categoryToDelete) {
          return { ...asset, selectedCategory: "" };
        }
        return asset;
      }));
    } catch (err) {
      console.error('Error deleting category:', err);
    }
  }, []);

  const handleAddLocation = useCallback(async (newLocation) => {
    try {
      const createdLocation = await axios.post('/api/locations', newLocation);
      setLocations((prev) => [...prev, createdLocation.data]);
    } catch (err) {
      console.error('Error adding location:', err);
    }
  }, []);

  const handleDeleteLocation = useCallback(async (locationToDelete) => {
    try {
      await axios.delete(`/api/locations/${locationToDelete}`);
      setLocations((prevLocations) => prevLocations.filter(location => location !== locationToDelete));
      setAssets((prevAssets) => prevAssets.map(asset => {
        if (asset.selectedLocation === locationToDelete) {
          return { ...asset, selectedLocation: "" };
        }
        return asset;
      }));
    } catch (err) {
      console.error('Error deleting location:', err);
    }
  }, []);

  const handleEditAsset = (asset) => {
    setAssetToEdit(asset);
    setIsModalOpen(true);
  };

  const handleOpenModal = () => {
    setAssetToEdit(null);
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
    setAssetToEdit(null);
  };

  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  const handleSort = (criteria) => {
    setSortCriteria(criteria);
  };

  const handleAllocate = useCallback(async (id, allocation) => {
    try {
      const assetToAllocate = assets.find(asset => asset._id === id);
      if (assetToAllocate) {
        const updatedQuantity = assetToAllocate.quantity - allocation >= 0 ? assetToAllocate.quantity - allocation : 0;
        const updatedAsset = await axios.put(`/api/assets/${id}`, { ...assetToAllocate, quantity: updatedQuantity });
        setAssets((prevAssets) =>
          prevAssets.map((asset) => (asset._id === id ? updatedAsset.data : asset))
        );
      }
    } catch (err) {
      console.error('Error allocating asset:', err);
    }
  }, [assets]);

  // Filter and sort assets based on search query and sort criteria
  const filteredAndSortedAssets = assets
    .filter(asset => asset.assetName.toLowerCase().includes(searchQuery.toLowerCase()))
    .sort((a, b) => {
      switch (sortCriteria) {
        case "dateAsc":
          return new Date(a.createdDate) - new Date(b.createdDate);
        case "dateDesc":
          return new Date(b.createdDate) - new Date(a.createdDate);
        case "quantityAsc":
          return a.quantity - b.quantity;
        case "quantityDesc":
          return b.quantity - a.quantity;
        case "borrowActive":
          return activeAssetIDs.includes(a._id) ? -1 : 1;
        case "borrowInactive":
          return activeAssetIDs.includes(a._id) ? 1 : -1;
        case "nameAsc":
          return a.assetName.localeCompare(b.assetName);
        case "nameDesc":
          return b.assetName.localeCompare(a.assetName);
        default:
          return 0;
      }
    });

  const totalAssets = filteredAndSortedAssets.length;
  const totalCost = filteredAndSortedAssets.reduce((acc, asset) => acc + parseFloat(asset.cost || 0), 0);
  const assetsForBorrowing = activeAssetIDs.length;

  return (
    <div className="asset-list-container">
      <InfoCards 
        totalAssets={totalAssets} 
        totalCost={`â‚±${totalCost.toFixed(2)}`} 
        assetsForBorrowing={assetsForBorrowing} 
      />
      <AssetSearchbar handleSearch={handleSearch} />
      <SortDropdown onSort={handleSort} />
      <AddAsset
        assetID={assetID}
        onAddAsset={handleAddAsset}
        categories={categories}  // Pass categories to AddAsset
        locations={locations} // Pass locations to AddAsset
        assetToEdit={assetToEdit}
        isModalOpen={isModalOpen}
        onCloseModal={handleCloseModal}
        onOpenModal={handleOpenModal}
      />
      <AssetTable
        assets={filteredAndSortedAssets}
        onDeleteAsset={handleDeleteAsset}
        onEditAsset={handleEditAsset}
        onAllocateAsset={handleAllocate}
        activeAssetIDs={activeAssetIDs}
        setActiveAssetIDs={setActiveAssetIDs}
      />
      <AssetCategory 
        onSaveCategory={handleAddCategory} 
        onDeleteCategory={handleDeleteCategory} // Pass delete handler
        categories={categories} // Pass updated categories
      />
      <AssetLocation 
        onSaveLocation={handleAddLocation} 
        onDeleteLocation={handleDeleteLocation} // Pass delete handler
        locations={locations} // Pass locations to AssetLocation
      />
    </div>
  );
};

export default AssetList;
