import { useState } from "react";
import PropTypes from "prop-types";

const UserInfo = ({ className = "" }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [name, setName] = useState("Vince Albert Juson");
  const [email, setEmail] = useState("vincejuson@ust.edu.ph");
  const [contact, setContact] = useState("09951778731");

  const handleEditClick = () => {
    setIsEditing(!isEditing);
  };

  const handleSaveClick = () => {
    setIsEditing(false);
    // Logic to save the updated information
  };

  return (
    <div
      className={`bg-[#fff] flex flex-col items-center justify-center p-4 max-w-full ${className}`}
    >
      <div className="w-full max-w-[334.5px] flex flex-col items-center text-center">
        <div className="flex flex-col gap-2">
          {isEditing ? (
            <input
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              className="text-[24px] font-bold border border-gray-300 rounded px-2 py-1"
            />
          ) : (
            <h3 className="text-[24px] font-bold">{name}</h3>
          )}
          <div className="text-[20px] text-[#787486]">
            Student
          </div>
        </div>
        <div className="w-full mt-4">
          <h3 className="text-[20px] font-bold">
            <span>Email: </span>
            {isEditing ? (
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="text-[20px] border border-gray-300 rounded px-2 py-1"
              />
            ) : (
              <span>{email}</span>
            )}
          </h3>
        </div>
        <div className="w-full mt-4">
          <h3 className="text-[20px] font-bold">
            <span>Contact: </span>
            {isEditing ? (
              <input
                type="text"
                value={contact}
                onChange={(e) => setContact(e.target.value)}
                className="text-[20px] border border-gray-300 rounded px-2 py-1"
              />
            ) : (
              <span>{contact}</span>
            )}
          </h3>
        </div>
        <div className="mt-4">
          <button
            className="bg-[#4e4e4e] text-white py-2 px-4 rounded"
            onClick={isEditing ? handleSaveClick : handleEditClick}
          >
            {isEditing ? "Save" : "Edit"}
          </button>
        </div>
      </div>
    </div>
  );
};

UserInfo.propTypes = {
  className: PropTypes.string,
};

export default UserInfo;
