import { useState } from "react";
import PropTypes from "prop-types";

const UserInfo = ({ className = "" }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [name, setName] = useState("Vince Albert Juson");
  const [email, setEmail] = useState("vincejuson@ust.edu.ph");
  const [contact, setContact] = useState("09951778731");

  const handleEditClick = () => {
    setIsEditing(!isEditing);
  };

  const handleSaveClick = () => {
    setIsEditing(false);
    // Logic to save the updated information
  };

  return (
    <div
      className={`self-stretch flex flex-col items-center justify-center text-center text-xl text-[#4b4b4b] font-[Poppins] ${className}`}
    >
      <div
        className="w-full max-w-md bg-[#ffebbb] rounded-t-md"
        style={{ paddingBottom: "0px", marginBottom: "0px" }}
      >
        <img
          src="/path/to/profile-image.png"
          alt="Profile"
          className="w-32 h-32 rounded-full mx-auto mt-4"
        />
      </div>
      <div
        className="w-full max-w-md bg-white px-12 pb-6 box-border flex flex-col items-center justify-center"
        style={{
          marginTop: "0px",
          borderRadius: "0 0 10px 10px",
          paddingTop: "0px",
        }}
      >
        <h3 className="text-lg font-bold text-[#4b4b4b] mb-4">
          {isEditing ? "Edit Profile" : "Profile"}
        </h3>
        <div className="w-full max-w-md">
          <div className="flex flex-col items-center justify-center mb-4">
            {isEditing ? (
              <input
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                className="text-lg font-bold text-[#4b4b4b] border border-gray-300 p-2 rounded-md w-full max-w-xs"
              />
            ) : (
              <h3 className="text-lg font-bold text-[#4b4b4b]">
                {name}
              </h3>
            )}
            <div className="text-sm text-[#787486] mt-1">Student</div>
          </div>
          <div className="mb-4">
            <h3 className="text-lg font-bold text-[#4b4b4b] mb-1">
              Email
            </h3>
            {isEditing ? (
              <input
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                className="text-lg border border-gray-300 p-2 rounded-md w-full max-w-xs"
              />
            ) : (
              <p className="text-lg">{email}</p>
            )}
          </div>
          <div>
            <h3 className="text-lg font-bold text-[#4b4b4b] mb-1">
              Contact
            </h3>
            {isEditing ? (
              <input
                type="text"
                value={contact}
                onChange={(e) => setContact(e.target.value)}
                className="text-lg border border-gray-300 p-2 rounded-md w-full max-w-xs"
              />
            ) : (
              <p className="text-lg">{contact}</p>
            )}
          </div>
          <div className="flex justify-center mt-4">
            <button
              className="bg-[#4e4e4e] text-white py-2 px-4 rounded-md"
              onClick={isEditing ? handleSaveClick : handleEditClick}
            >
              {isEditing ? "Save Profile" : "Edit Profile"}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

UserInfo.propTypes = {
  className: PropTypes.string,
};

export default UserInfo;
