import React, { useState, useCallback } from "react";
import AssetSearchbar from "./components/assetsearchbar";
import AssetTable from "./components/assettable";
import InfoCards from "./components/infocards";
import AddAsset from "./components/addasset";
import AssetCategory from "./components/addcategory";
import AssetLocation from "./components/assetlocation";
import EditAssetModal from "./components/editasset";

const AssetList = () => {
  const [assets, setAssets] = useState([]);
  const [categories, setCategories] = useState([]);
  const [locations, setLocations] = useState([]);
  const [assetID, setAssetID] = useState(1); // Initialize with starting ID
  const [selectedAsset, setSelectedAsset] = useState(null);
  const [isEditModalOpen, setIsEditModalOpen] = useState(false);

  const handleAddAsset = useCallback((newAsset) => {
    setAssets((prevAssets) => [...prevAssets, newAsset]);
    setAssetID((prevID) => prevID + 1); // Increment asset ID
  }, []);

  const handleDeleteAsset = useCallback((id) => {
    setAssets((prevAssets) => prevAssets.filter((asset) => asset.assetID !== id));
  }, []);

  const handleEditAsset = useCallback((asset) => {
    setSelectedAsset(asset);
    setIsEditModalOpen(true);
  }, []);

  const handleSaveAsset = useCallback((updatedAsset) => {
    setAssets((prevAssets) =>
      prevAssets.map((asset) => (asset.assetID === updatedAsset.assetID ? updatedAsset : asset))
    );
  }, []);

  return (
    <div>
      <AssetSearchbar />
      <AssetTable assets={assets} onDeleteAsset={handleDeleteAsset} onEditAsset={handleEditAsset} />
      <AddAsset categories={categories} locations={locations} onAddAsset={handleAddAsset} />
      <AssetCategory categories={categories} setCategories={setCategories} />
      <AssetLocation locations={locations} setLocations={setLocations} />
      <InfoCards assets={assets} />

      <EditAssetModal
        isOpen={isEditModalOpen}
        onClose={() => setIsEditModalOpen(false)}
        asset={selectedAsset}
        categories={categories}
        locations={locations}
        onSaveAsset={handleSaveAsset}
      />
    </div>
  );
};

export default AssetList;
