import React, { useState } from 'react';

// Button component
const Button = ({ onClick, children, className }) => (
  <button
    className={`px-1 py-2 rounded-lg ${className}`}
    onClick={onClick}
  >
    {children}
  </button>
);

// Modal component
const Modal = ({ isOpen, onClose, onSave }) => {
  const [locationName, setLocationName] = useState('');

  const handleSave = () => {
    const trimmedName = locationName.trim();
    if (trimmedName) {
      onSave(trimmedName);
      setLocationName('');
      onClose();
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
      <div className="bg-white p-6 rounded-lg w-[400px] shadow-lg">
        <h2 className="text-lg font-semibold mb-4">Add Location</h2>
        <input
          type="text"
          placeholder="Location Name"
          value={locationName}
          onChange={(e) => setLocationName(e.target.value)}
          className="border p-2 rounded mb-4 w-full"
        />
        <div className="flex justify-end gap-4">
          <Button className="bg-gray-300" onClick={onClose}>
            Cancel
          </Button>
          <Button className="bg-blue-500 text-white" onClick={handleSave}>
            Add Location
          </Button>
        </div>
      </div>
    </div>
  );
};

// AssetLocation component
const AssetLocation = ({ onSaveLocation }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleOpenModal = () => setIsModalOpen(true);
  const handleCloseModal = () => setIsModalOpen(false);

  return (
    <div>
      <Button
        className="flex w-[112px] h-[41px] justify-center items-center bg-[#FF4B4B] text-[#343B3F] font-roboto text-[10px] font-semibold"
        onClick={handleOpenModal}
      >
        Asset Location
      </Button>

      <Modal
        isOpen={isModalOpen}
        onClose={handleCloseModal}
        onSave={onSaveLocation}
      />
    </div>
  );
};

export default AssetLocation;
