import { useState } from "react";
import PropTypes from "prop-types";

const UserInfo = ({ className = "" }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [name, setName] = useState("Vince Albert Juson");
  const [email, setEmail] = useState("vincejuson@ust.edu.ph");
  const [contact, setContact] = useState("09951778731");

  const handleEditClick = () => setIsEditing(true);
  const handleSaveClick = () => setIsEditing(false);

  return (
    <div
      className={`bg-white flex flex-col items-center p-6 gap-6 text-center text-gray-700 ${className}`}
    >
      <div className="flex flex-col items-center gap-4">
        {isEditing ? (
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="text-lg font-bold border-b border-gray-300"
          />
        ) : (
          <h3 className="text-2xl font-bold">{name}</h3>
        )}
        <div className="text-lg text-gray-500">Student</div>
      </div>
      <div className="flex flex-col gap-4 w-full">
        <div>
          <b>Email:</b> {isEditing ? (
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="text-lg border-b border-gray-300"
            />
          ) : (
            <span>{email}</span>
          )}
        </div>
        <div>
          <b>Contact:</b> {isEditing ? (
            <input
              type="text"
              value={contact}
              onChange={(e) => setContact(e.target.value)}
              className="text-lg border-b border-gray-300"
            />
          ) : (
            <span>{contact}</span>
          )}
        </div>
      </div>
      <button
        className="bg-gray-800 text-white py-2 px-4 rounded"
        onClick={isEditing ? handleSaveClick : handleEditClick}
      >
        {isEditing ? "Save" : "Edit"}
      </button>
    </div>
  );
};

UserInfo.propTypes = {
  className: PropTypes.string,
};

export default UserInfo;
