import React, { useState } from 'react';
import InfoCards from './infocards'; // Adjust the path as needed
import AddAsset from './addasset'; // Adjust the path as needed
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash } from '@fortawesome/free-solid-svg-icons';

const AssetManagement = () => {
  const [assets, setAssets] = useState([]);

  const handleAddAsset = (newAsset) => {
    setAssets((prevAssets) => [...prevAssets, newAsset]);
  };

  const handleDeleteAsset = (assetID) => {
    setAssets((prevAssets) => prevAssets.filter(asset => asset.assetID !== assetID));
  };

  const totalAssets = assets.length;
  const totalCost = `â‚±${assets.reduce((total, asset) => total + parseFloat(asset.cost || 0), 0).toFixed(2)}`;
  const assetsForBorrowing = assets.filter(asset => asset.isBorrowed).length;

  return (
    <div>
      <InfoCards
        totalAssets={totalAssets}
        totalCost={totalCost}
        assetsForBorrowing={assetsForBorrowing}
        prize="N/A" // Update as needed
      />
      <div className="relative p-4">
        <AddAsset onAddAsset={handleAddAsset} />
        <div className="flex flex-col items-start gap-1 p-4 bg-white border-radius-8 h-full">
          <h2 className="text-xl font-semibold mb-4">Asset Management</h2>
          <div className="w-full">
            <table className="w-full border-collapse">
              <thead>
                <tr className="border-b">
                  <th className="p-2">Asset ID</th>
                  <th className="p-2">Created Date</th>
                  <th className="p-2">Asset Name</th>
                  <th className="p-2">Asset Details</th>
                  <th className="p-2">Category</th>
                  <th className="p-2">Subcategory</th>
                  <th className="p-2">Location</th>
                  <th className="p-2">Quantity</th>
                  <th className="p-2">â‚± Cost</th>
                  <th className="p-2">Delete</th>
                </tr>
              </thead>
              <tbody className="text-center">
                {assets.length > 0 ? (
                  assets.map((asset) => (
                    <tr key={asset.assetID} className="border-b">
                      <td className="p-2">{asset.assetID}</td>
                      <td className="p-2">{asset.createdDate}</td>
                      <td className="p-2">{asset.assetName}</td>
                      <td className="p-2">{asset.assetDetails}</td>
                      <td className="p-2">{asset.selectedCategory}</td>
                      <td className="p-2">{asset.selectedSubCategory}</td>
                      <td className="p-2">{asset.selectedLocation}</td>
                      <td className="p-2">{asset.quantity}</td>
                      <td className="p-2">{asset.cost}</td>
                      <td className="p-2">
                        <button
                          className="bg-white-500 text-black p-1 rounded"
                          onClick={() => handleDeleteAsset(asset.assetID)}
                        >
                          <FontAwesomeIcon icon={faTrash} />
                        </button>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="10" className="p-2 text-center">No assets available.</td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AssetManagement;
