import React from 'react';
import moment from 'moment';

const AssetDetailsModal = ({ selectedAsset, onClose }) => {
  if (!selectedAsset) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
      <div className="bg-white p-6 rounded-lg max-w-2xl w-full">
        <h2 className="text-2xl font-bold mb-4">{selectedAsset.assetName}</h2>
        <div className="grid grid-cols-2 gap-4">
          <p><strong>ID:</strong> {selectedAsset.asset_id}</p>
          <p><strong>Date Created:</strong> {moment(selectedAsset.createdDate).format('MM/DD/YYYY')}</p>
          <p><strong>Category:</strong> {selectedAsset.category}</p>
          <p><strong>Location:</strong> {selectedAsset.location}</p>
          <p><strong>Quantity:</strong> {selectedAsset.quantity}</p>
          <p><strong>Cost:</strong> â‚±{selectedAsset.cost}</p>
          <p><strong>Type:</strong> {selectedAsset.type}</p>
          <p><strong>Status:</strong> {selectedAsset.is_active ? 'Active' : 'Inactive'}</p>
        </div>
        <p className="mt-4"><strong>Details:</strong> {selectedAsset.assetDetails}</p>
        {selectedAsset.image && (
          <img src={selectedAsset.image} alt={selectedAsset.assetName} className="mt-4 max-h-48 object-contain" />
        )}
        <button 
          className="mt-6 bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
          onClick={onClose}
        >
          Close
        </button>
      </div>
    </div>
  );
};

export default AssetDetailsModal;
