import React, { useState, useCallback } from 'react';
import './assetlist.css';
import AssetSearchbar from "./components/assetlists/assetsearchbar";
import AssetTable from "./components/assetlists/assettable";
import InfoCards from "./components/assetlists/infocards";
import AddAsset from "./components/assetlists/addasset";
import AssetCategory from "./components/assetlists/addcategory";
import AssetLocation from "./components/assetlists/addlocation";
import SortDropdown from "./components/assetlists/sortdropdown";

const AssetList = () => {
  const [assets, setAssets] = useState([]);
  const [categories, setCategories] = useState([]);
  const [locations, setLocations] = useState([]);
  const [assetID, setAssetID] = useState(1);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [activeAssetIDs, setActiveAssetIDs] = useState([]);
  const [searchQuery, setSearchQuery] = useState("");
  const [sortCriteria, setSortCriteria] = useState("");
  const [selectedAsset, setSelectedAsset] = useState(null); // State for selected asset

  const handleAddAsset = useCallback((newAsset) => {
    setAssets((prevAssets) => {
      setAssetID((prevID) => prevID + 1);
      return [...prevAssets, { ...newAsset, assetID }];
    });
    setIsModalOpen(false);
  }, [assetID]);

  const handleDeleteAsset = useCallback((id) => {
    setAssets((prevAssets) => {
      const updatedAssets = prevAssets.filter(asset => asset.assetID !== id);
      setActiveAssetIDs((prevActiveIDs) => prevActiveIDs.filter(assetID => assetID !== id));
      return updatedAssets;
    });
  }, []);

  const handleAddCategory = useCallback((newCategory) => {
    setCategories((prev) => [...prev, newCategory]);
  }, []);

  const handleDeleteCategory = useCallback((categoryToDelete) => {
    setCategories((prevCategories) => prevCategories.filter(category => category !== categoryToDelete));
    setAssets((prevAssets) => prevAssets.map(asset => {
      if (asset.selectedCategory === categoryToDelete) {
        return { ...asset, selectedCategory: "" };
      }
      return asset;
    }));
  }, []);

  const handleAddLocation = useCallback((newLocation) => {
    setLocations((prev) => [...prev, newLocation]);
  }, []);

  const handleDeleteLocation = useCallback((locationToDelete) => {
    setLocations((prevLocations) => prevLocations.filter(location => location !== locationToDelete));
    setAssets((prevAssets) => prevAssets.map(asset => {
      if (asset.selectedLocation === locationToDelete) {
        return { ...asset, selectedLocation: "" };
      }
      return asset;
    }));
  }, []);

  const handleOpenModal = () => {
    setIsModalOpen(true);
  };

  const handleCloseModal = () => {
    setIsModalOpen(false);
  };

  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  const handleSort = (criteria) => {
    setSortCriteria(criteria);
  };

  const handleAllocate = useCallback((id, allocation) => {
    setAssets((prevAssets) =>
      prevAssets.map((asset) =>
        asset.assetID === id
          ? {
              ...asset,
              quantity: asset.quantity - allocation >= 0 ? asset.quantity - allocation : 0,
            }
          : asset
      )
    );
  }, []);

  const handleViewAssetDetails = (asset) => {
    setSelectedAsset(asset); // Set selected asset
  };

  const filteredAndSortedAssets = assets
    .filter(asset => asset.assetName.toLowerCase().includes(searchQuery.toLowerCase()))
    .sort((a, b) => {
      switch (sortCriteria) {
        case "dateAsc":
          return new Date(a.createdDate) - new Date(b.createdDate);
        case "dateDesc":
          return new Date(b.createdDate) - new Date(a.createdDate);
        case "quantityAsc":
          return a.quantity - b.quantity;
        case "quantityDesc":
          return b.quantity - a.quantity;
        case "borrowActive":
          return activeAssetIDs.includes(a.assetID) ? -1 : 1;
        case "borrowInactive":
          return activeAssetIDs.includes(a.assetID) ? 1 : -1;
        case "nameAsc":
          return a.assetName.localeCompare(b.assetName);
        case "nameDesc":
          return b.assetName.localeCompare(a.assetName);
        default:
          return 0;
      }
    });

  const totalAssets = filteredAndSortedAssets.length;
  const totalCost = filteredAndSortedAssets.reduce((acc, asset) => acc + parseFloat(asset.cost || 0), 0);
  const assetsForBorrowing = activeAssetIDs.length;

  return (
    <div className="asset-list-container">
      <InfoCards 
        totalAssets={totalAssets} 
        totalCost={`â‚±${totalCost.toFixed(2)}`} 
        assetsForBorrowing={assetsForBorrowing} 
      />
      <AssetSearchbar handleSearch={handleSearch} />
      <SortDropdown onSort={handleSort} />
      <AddAsset
        assetID={assetID}
        onAddAsset={handleAddAsset}
        categories={categories}
        locations={locations}
        isModalOpen={isModalOpen}
        onCloseModal={handleCloseModal}
        onOpenModal={handleOpenModal}
      />
      <AssetTable
        assets={filteredAndSortedAssets}
        onDeleteAsset={handleDeleteAsset}
        onAllocateAsset={handleAllocate}
        activeAssetIDs={activeAssetIDs}
        setActiveAssetIDs={setActiveAssetIDs}
        onViewAssetDetails={handleViewAssetDetails} // Pass the view details handler
      />
      <AssetCategory 
        onSaveCategory={handleAddCategory} 
        onDeleteCategory={handleDeleteCategory}
        categories={categories}
      />
      <AssetLocation 
        onSaveLocation={handleAddLocation} 
        onDeleteLocation={handleDeleteLocation}
        locations={locations}
      />

      {/* Modal for asset details */}
      {selectedAsset && (
        <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
          <div className="relative bg-white p-8 rounded-lg shadow-xl w-full max-w-3xl mx-4 lg:mx-8">
            <button
              className="absolute top-4 right-4 text-gray-600 hover:text-gray-800 transition-colors duration-200"
              onClick={() => setSelectedAsset(null)}
              aria-label="Close modal"
            >
              <svg
                className="w-6 h-6"
                fill="none"
                stroke="currentColor"
                viewBox="0 0 24 24"
                xmlns="http://www.w3.org/2000/svg"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M6 18L18 6M6 6l12 12"
                />
              </svg>
            </button>
            <h2 className="text-3xl font-bold mb-6 text-gray-800">{selectedAsset.assetName}</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
              <div>
                <p className="text-lg font-semibold text-gray-700"><strong>ID:</strong> {selectedAsset.assetID}</p>
                <p className="text-lg font-semibold text-gray-700"><strong>Date Created:</strong> {selectedAsset.createdDate}</p>
                <p className="text-lg font-semibold text-gray-700"><strong>Quantity:</strong> {selectedAsset.quantity}</p>
                <p className="text-lg font-semibold text-gray-700"><strong>Description:</strong> {selectedAsset.description}</p>
                <p className="text-lg font-semibold text-gray-700"><strong>Category:</strong> {selectedAsset.selectedCategory}</p>
                <p className="text-lg font-semibold text-gray-700"><strong>Location:</strong> {selectedAsset.selectedLocation}</p>
                <p className="text-lg font-semibold text-gray-700"><strong>Cost:</strong> {selectedAsset.cost}</p>
              </div>
              <div className="flex justify-center items-center">
                <img
                  src={selectedAsset.image}
                  alt={selectedAsset.assetName}
                  className="w-full h-auto max-h-80 object-cover rounded-lg shadow-md"
                />
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AssetList;
