import { useState } from "react";
import PropTypes from "prop-types";

const UserInfo = ({ className = "" }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [name, setName] = useState("Vince Albert Juson");
  const [email, setEmail] = useState("vincejuson@ust.edu.ph");
  const [contact, setContact] = useState("09951778731");

  const handleEditClick = () => {
    setIsEditing(!isEditing);
  };

  const handleSaveClick = () => {
    setIsEditing(false);
    // Logic to save the updated information
  };

  return (
    <div
      className={`bg-white flex flex-col items-center justify-center p-6 gap-6 text-center text-gray-700 font-Poppins ${className}`}
    >
      <div className="w-full max-w-lg">
        {isEditing ? (
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="text-lg font-bold border-b border-gray-300 p-2 w-full"
          />
        ) : (
          <h3 className="text-2xl font-bold">{name}</h3>
        )}
        <div className="text-xl text-gray-500 mt-2">Student</div>
      </div>

      <div className="w-full max-w-lg">
        <div className="text-left">
          <p className="font-bold">Email:</p>
          {isEditing ? (
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="text-lg border-b border-gray-300 p-2 w-full"
            />
          ) : (
            <p className="text-lg">{email}</p>
          )}
        </div>
      </div>

      <div className="w-full max-w-lg">
        <div className="text-left">
          <p className="font-bold">Contact:</p>
          {isEditing ? (
            <input
              type="text"
              value={contact}
              onChange={(e) => setContact(e.target.value)}
              className="text-lg border-b border-gray-300 p-2 w-full"
            />
          ) : (
            <p className="text-lg">{contact}</p>
          )}
        </div>
      </div>

      <button
        className="bg-gray-800 text-white py-2 px-4 rounded"
        onClick={isEditing ? handleSaveClick : handleEditClick}
      >
        {isEditing ? "Save" : "Edit"}
      </button>
    </div>
  );
};

UserInfo.propTypes = {
  className: PropTypes.string,
};

export default UserInfo;
