import React, { useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faTrash, faEdit } from "@fortawesome/free-solid-svg-icons";

const AssetTable = ({ assets, onDeleteAsset, onEditAsset }) => {
    const [activeAssetIDs, setActiveAssetIDs] = useState([]);
    const [selectedImage, setSelectedImage] = useState(null);

    const handleBorrowClick = (assetID) => {
        setActiveAssetIDs((prevIDs) =>
            prevIDs.includes(assetID)
                ? prevIDs.filter((id) => id !== assetID)
                : [...prevIDs, assetID]
        );
    };

    const handleImageClick = (image) => {
        setSelectedImage(image);
    };

    const handleCloseImageModal = () => {
        setSelectedImage(null);
    };

    return (
        <div className="relative p-4 w-full bg-white border border-gray-200 rounded-lg shadow-md">
            <table className="table-auto w-full border-collapse">
                <thead>
                    <tr>
                        <th>ID</th>
                        <th>Date Created</th>
                        <th>Asset</th>
                        <th>Details</th>
                        <th>Category</th>
                        <th>Location</th>
                        <th>Quantity</th>
                        <th>Cost</th>
                        <th>Borrow</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {assets.map((asset) => (
                        <tr key={asset.assetID}>
                            <td>{asset.assetID}</td>
                            <td>{asset.createdDate}</td>
                            <td>
                                <div className="flex items-center">
                                    {asset.image && (
                                        <img
                                            src={asset.image}
                                            alt={asset.assetName}
                                            className="h-8 w-8 object-cover mr-2 cursor-pointer"
                                            onClick={() => handleImageClick(asset.image)}
                                        />
                                    )}
                                    {asset.assetName}
                                </div>
                            </td>
                            <td>{asset.assetDetails}</td>
                            <td>{asset.selectedCategory}</td>
                            <td>{asset.selectedLocation}</td>
                            <td>{asset.quantity}</td>
                            <td>{`â‚±${asset.cost}`}</td>
                            <td>
                                <div className="flex items-center">
                                    <button
                                        className={`w-4 h-4 rounded-full border-2 border-gray-400 ${
                                            activeAssetIDs.includes(asset.assetID)
                                                ? "bg-blue-500"
                                                : "bg-white"
                                        } shadow-md transition-transform transform ${
                                            activeAssetIDs.includes(asset.assetID)
                                                ? "scale-110"
                                                : "scale-100"
                                        }`}
                                        onClick={() => handleBorrowClick(asset.assetID)}
                                        aria-label={`Borrow ${asset.assetID}`}
                                    ></button>
                                    <span className="ml-2 text-sm text-gray-600"></span>
                                </div>
                            </td>
                            <td>
                                <div className="flex items-center space-x-2">
                                    <div className="flex items-center">
                                        <button
                                            className="text-blue-600 flex items-center space-x-1"
                                            onClick={() => onEditAsset(asset)}
                                        >
                                            <FontAwesomeIcon icon={faEdit} />
                                            <span>Edit</span>
                                        </button>
                                    </div>
                                    <div className="flex items-center">
                                        <button
                                            className="text-red-600 flex items-center space-x-1"
                                            onClick={() => onDeleteAsset(asset.assetID)}
                                        >
                                            <FontAwesomeIcon icon={faTrash} />
                                            <span>Delete</span>
                                        </button>
                                    </div>
                                </div>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {/* Modal for enlarged image */}
            {selectedImage && (
                <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-75">
                    <div className="relative bg-white p-4 rounded-lg shadow-lg">
                        <img
                            src={selectedImage}
                            alt="Enlarged Asset"
                            className="h-96 w-96 object-cover"
                        />
                        <button
                            className="absolute top-2 right-2 text-gray-600"
                            onClick={handleCloseImageModal}
                        >
                            Close
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default AssetTable;
