import React, { useState, useCallback, useMemo } from 'react';
import AddCategory from './addcategory';
import AssetLocation from './assetlocation';

const InputField = ({ label, value, onChange, type = 'text', readOnly = false, prefix }) => (
  <div className="mb-4 relative">
    <label className="block text-sm font-medium mb-2">{label}</label>
    <input
      type={type}
      value={value}
      onChange={onChange}
      readOnly={readOnly}
      className={`border p-2 rounded w-full ${prefix ? 'pl-8' : ''}`}
      placeholder={prefix ? '' : label}
    />
    {prefix && (
      <span className="absolute left-2 top-1/2 transform -translate-y-2/2 text-black-500">
        {prefix}
      </span>
    )}
  </div>
);

const SelectField = ({ label, value, onChange, options = [] }) => (
  <div className="mb-4">
    <label className="block text-sm font-medium mb-2">{label}</label>
    <select
      value={value}
      onChange={onChange}
      className="border p-2 rounded w-full"
    >
      <option value="">Select {label}</option>
      {Array.isArray(options) && options.length > 0 ? (
        options.map((option, index) => (
          <option key={index} value={option}>
            {option}
          </option>
        ))
      ) : (
        <option value="">No {label.toLowerCase()} available</option>
      )}
    </select>
  </div>
);

const Button = ({ className, onClick, children }) => (
	<button
	  className={`flex w-[112px] h-[41px] p-4 justify-center items-center gap-4 flex-shrink-0 rounded-lg ${className}`}
	  onClick={onClick}
	>
	  {children}
	</button>
  );
  
const formatDate = (date) => {
  const options = { year: 'numeric', month: '2-digit', day: '2-digit' };
  return new Intl.DateTimeFormat('en-US', options).format(new Date(date));
};

const Modal = ({
  isOpen,
  onClose,
  assetID,
  createdDate,
  categories = [],
  selectedCategory,
  setSelectedCategory,
  selectedSubCategory,
  setSelectedSubCategory,
  assetName,
  setAssetName,
  assetDetails,
  setAssetDetails,
  quantity,
  setQuantity,
  cost,
  setCost,
  locations = [],
  selectedLocation,
  setSelectedLocation,
  onAddAsset
}) => {
  const subCategoryOptions = useMemo(() => {
    if (!selectedCategory) return [];
    const category = categories.find(cat => cat.category === selectedCategory);
    return category ? category.subCategory || [] : [];
  }, [selectedCategory, categories]);

  if (!isOpen) return null;

  const handleAddAsset = () => {
    if (typeof onAddAsset !== 'function') {
      console.error('onAddAsset is not a function');
      return;
    }

    const newAsset = {
      assetID,
      createdDate: formatDate(createdDate),
      assetName,
      assetDetails,
      selectedCategory,
      selectedSubCategory,
      selectedLocation,
      quantity,
      cost
    };
    console.log('Submitting new asset from modal:', newAsset); // Debugging line
    onAddAsset(newAsset); // Pass the new asset to the handler
    onClose();
  };

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
      <div className="bg-white p-6 rounded-lg w-[400px] shadow-lg">
        <h2 className="text-lg font-semibold mb-4">Add Asset</h2>
        <InputField label="Asset ID" value={assetID} readOnly />
        <InputField label="Created Date" value={formatDate(createdDate)} readOnly />
        <InputField label="Asset Name" value={assetName} onChange={(e) => setAssetName(e.target.value)} />
        <InputField label="Asset Details" value={assetDetails} onChange={(e) => setAssetDetails(e.target.value)} />
        <SelectField label="Category" value={selectedCategory} onChange={(e) => setSelectedCategory(e.target.value)} options={categories.map(cat => cat.category)} />
        <SelectField label="Subcategory" value={selectedSubCategory} onChange={(e) => setSelectedSubCategory(e.target.value)} options={subCategoryOptions} />
        <SelectField label="Location" value={selectedLocation} onChange={(e) => setSelectedLocation(e.target.value)} options={locations} />
        <InputField label="Quantity" type="number" value={quantity} onChange={(e) => setQuantity(Number(e.target.value))} />
        <InputField label="Cost" value={cost} onChange={(e) => setCost(e.target.value.replace(/[^0-9]/g, ""))} prefix="₱" />
        <div className="flex justify-end gap-4 mt-4">
          <Button className="bg-gray-300" onClick={onClose}>Cancel</Button>
          <Button className="bg-green-500 text-white" onClick={handleAddAsset}>Add Asset</Button>
        </div>
      </div>
    </div>
  );
};


const AddAsset = ({ onAddAsset }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [assetID, setAssetID] = useState(1); // Initialize with starting ID
  const [createdDate, setCreatedDate] = useState(new Date());
  const [assetName, setAssetName] = useState("");
  const [assetDetails, setAssetDetails] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [cost, setCost] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("");
  const [selectedSubCategory, setSelectedSubCategory] = useState("");
  const [categories, setCategories] = useState([]);
  const [locations, setLocations] = useState([]);
  const [selectedLocation, setSelectedLocation] = useState("");

  const handleOpenModal = useCallback(() => {
    setCreatedDate(new Date());
    setIsModalOpen(true);
  }, []);

  const handleCloseModal = useCallback(() => {
    setIsModalOpen(false);
  }, []);

  const handleAddCategory = useCallback((newCategoryData) => {
    setCategories(prev => [...prev, newCategoryData]);
  }, []);

  const handleAddLocation = useCallback((newLocation) => {
    setLocations(prev => [...prev, newLocation]);
  }, []);

  const handleAddAsset = useCallback((newAsset) => {
    onAddAsset(newAsset);
    setAssetID(prevID => prevID + 1); // Increment the ID for next asset
  }, [onAddAsset]);

  return (
    <div>
      <Button
        className="bg-[#68C231] text-[#343B3F] text-[10px] font-semibold"
        onClick={handleOpenModal}
      >
        Add Asset
      </Button>
      <Modal
        isOpen={isModalOpen}
        onClose={handleCloseModal}
        assetID={assetID}
        createdDate={createdDate}
        categories={categories}
        selectedCategory={selectedCategory}
        setSelectedCategory={setSelectedCategory}
        selectedSubCategory={selectedSubCategory}
        setSelectedSubCategory={setSelectedSubCategory}
        assetName={assetName}
        setAssetName={setAssetName}
        assetDetails={assetDetails}
        setAssetDetails={setAssetDetails}
        quantity={quantity}
        setQuantity={setQuantity}
        cost={cost}
        setCost={setCost}
        locations={locations}
        selectedLocation={selectedLocation}
        setSelectedLocation={setSelectedLocation}
        onAddAsset={handleAddAsset} // Pass the updated handler
      />
      <AddCategory onSaveCategory={handleAddCategory} />
      <AssetLocation onSaveLocation={handleAddLocation} />
    </div>
  );
};

export default AddAsset;
