app.post("/api/users/request-access", async (req, res) => {
  const { email } = req.body;
  try {
    const result = await db.createRecord("Users", { email });
    res.status(201).json(result[0]);  // Send the created user as response
  } catch (err) {
    res.status(500).json({ error: "Error creating user", details: err });
  }
});
