import { useEffect, useState } from "react";
import { gapi } from "gapi-script";
import LogoutButton from "../components/logout";

const clientId =
  "1072140054426-iucuc7c784kr4bvat2nkv8mvd865005s.apps.googleusercontent.com";

function Dashboard() {
  const [emails, setEmails] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Initialize Google API Client
  useEffect(() => {
    function start() {
      gapi.client.init({
        clientId: clientId,
        scope: "",
      });
    }

    gapi.load("client:auth2", start);
  }, []);

  // Fetch emails from backend API
  useEffect(() => {
    const fetchEmails = async () => {
      try {
        const response = await fetch("http://localhost:5000/api/users/request-access-emails");
        if (!response.ok) {
          throw new Error("Failed to fetch emails");
        }
        const result = await response.json();
        setEmails(result);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchEmails();
  }, []);

  // Handle loading and error states
  if (loading) {
    return <div className="text-center">Loading...</div>;
  }

  if (error) {
    return <div className="text-center text-red-600">{error}</div>;
  }

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-white">
      <header className="text-center">
        <h1 className="text-4xl font-bold text-gray-900 mb-4">Dashboard</h1>
        <p className="text-lg text-gray-600 mb-8">Welcome to your dashboard!</p>
        <LogoutButton />
      </header>

      {/* Display fetched emails in table form */}
      <div className="overflow-x-auto w-full">
        <h2 className="text-2xl font-bold mb-4">Emails Requesting Access:</h2>
        {emails.length > 0 ? (
          <table className="table-auto border-collapse w-full">
            <thead>
              <tr className="bg-gray-200">
                <th className="px-4 py-2 border">Email</th>
              </tr>
            </thead>
            <tbody>
              {emails.map((item, index) => (
                <tr key={index} className="text-center">
                  <td className="border px-4 py-2">{item.email}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <div>No emails available</div>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
