import React from 'react';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPrint } from "@fortawesome/free-solid-svg-icons";
import moment from 'moment';

const PrintAssetsPDF = ({ assets }) => {
  const handlePrint = () => {
    window.print();
  };

  const renderAssetDetail = (label, value) => {
    if (value !== undefined && value !== null && value !== '') {
      return <p><strong>{label}:</strong> {value}</p>;
    }
    return null;
  };

  return (
    <>
      <button
        onClick={handlePrint}
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded flex items-center print:hidden"
      >
        <FontAwesomeIcon icon={faPrint} className="mr-2" />
        Print Asset Inventory
      </button>

      <div className="hidden print:block">
        <h1 className="text-3xl font-bold mb-6 text-center">Asset Inventory</h1>
        {assets.map((asset) => (
          <div key={asset.asset_id} className="mb-8 pb-4 border-b">
            <h2 className="text-2xl font-semibold mb-2">{asset.assetName}</h2>
            <div className="grid grid-cols-2 gap-4">
              <div>
                {renderAssetDetail("ID", asset.asset_id)}
                {renderAssetDetail("Date Created", asset.createdDate ? moment(asset.createdDate).format('MM/DD/YYYY') : 'N/A')}
                {renderAssetDetail("Category", asset.category)}
                {renderAssetDetail("Location", asset.location)}
                {renderAssetDetail("Quantity", asset.quantity)}
                {renderAssetDetail("Allocated Quantity", asset.allocatedQuantity)}
                {renderAssetDetail("Cost", asset.cost ? `₱${asset.cost}` : 'N/A')}
              </div>
              <div>
                {renderAssetDetail("Type", asset.type)}
                {renderAssetDetail("Status", asset.is_active ? 'Active' : 'Inactive')}
                {renderAssetDetail("Supplier", asset.supplier)}
                {renderAssetDetail("Serial Number", asset.serialNumber)}
                {renderAssetDetail("Warranty Expiration", asset.warrantyExpiration ? moment(asset.warrantyExpiration).format('MM/DD/YYYY') : 'N/A')}
                {renderAssetDetail("Brand", asset.brand)}
                {renderAssetDetail("Model", asset.model)}
              </div>
            </div>
            {renderAssetDetail("Details", asset.assetDetails)}
            {asset.image && (
              <img src={asset.image} alt={asset.assetName} className="mt-4 max-w-xs mx-auto" />
            )}
          </div>
        ))}
      </div>
    </>
  );
};

export default PrintAssetsPDF;
