import React, { useState } from 'react';
import AddAsset from './addasset';

const AssetTable = () => {
  const [assets, setAssets] = useState([]);

  const handleAddAsset = (newAsset) => {
    setAssets((prevAssets) => [...prevAssets, newAsset]);
    console.log('New asset added:', newAsset); // Debugging line
  };

  return (
    <div className="flex flex-col items-start gap-6 p-4 w-[1477px] h-[1050px] border-radius-8 bg-white">
      <h2 className="text-xl font-semibold mb-4">Asset Management</h2>
      <AddAsset onAddAsset={handleAddAsset} />
      <div className="w-full">
        {assets.length > 0 ? (
          <table className="w-full border-collapse">
            <thead>
              <tr className="border-b">
                <th className="p-2">Asset ID</th>
                <th className="p-2">Created Date</th>
                <th className="p-2">Asset Name</th>
                <th className="p-2">Asset Details</th>
                <th className="p-2">Category</th>
                <th className="p-2">Subcategory</th>
                <th className="p-2">Location</th>
                <th className="p-2">Quantity</th>
                <th className="p-2">Cost</th>
              </tr>
            </thead>
            <tbody>
              {assets.map((asset, index) => (
                <tr key={index} className="border-b">
                  <td className="p-2">{asset.assetID}</td>
                  <td className="p-2">{asset.createdDate}</td>
                  <td className="p-2">{asset.assetName}</td>
                  <td className="p-2">{asset.assetDetails}</td>
                  <td className="p-2">{asset.selectedCategory}</td>
                  <td className="p-2">{asset.selectedSubCategory}</td>
                  <td className="p-2">{asset.selectedLocation}</td>
                  <td className="p-2">{asset.quantity}</td>
                  <td className="p-2">{asset.cost}</td>
                </tr>
              ))}
            </tbody>
          </table>
        ) : (
          <p>No assets available.</p>
        )}
      </div>
    </div>
  );
};

export default AssetTable;
