import React, { useState } from 'react';

const InfoCards = () => {
  const [assets, setAssets] = useState([]);

  const handleAddAsset = (newAsset) => {
    setAssets((prevAssets) => [...prevAssets, newAsset]);
  };

  const totalAssets = assets.length;
  const totalCost = assets.reduce((sum, asset) => sum + asset.cost, 0);
  const assetsForBorrowing = assets.filter(asset => asset.isBorrowable).length;
  const prize = assets.reduce((sum, asset) => sum + (asset.prize || 0), 0); // Example calculation for prize

  return (
    <div className="flex justify-between p-2">
      {/* Total Assets Card */}
      <div className="bg-white border border-gray-200 rounded-lg shadow-md p-6 m-4 w-full sm:w-1/2 md:w-1/3 lg:w-1/4">
        <h2 className="text-xl font-semibold text-gray-700">Total Assets</h2>
        <p className="text-3xl font-bold text-gray-900 mt-2">{totalAssets}</p>
      </div>

      {/* Total Cost Card */}
      <div className="bg-white border border-gray-200 rounded-lg shadow-md p-6 m-4 w-full sm:w-1/2 md:w-1/3 lg:w-1/4">
        <h2 className="text-xl font-semibold text-gray-700">Total Cost</h2>
        <p className="text-3xl font-bold text-gray-900 mt-2">{totalCost}</p>
      </div>

      {/* Assets for Borrowing Card */}
      <div className="bg-white border border-gray-200 rounded-lg shadow-md p-6 m-4 w-full sm:w-1/2 md:w-1/3 lg:w-1/4">
        <h2 className="text-xl font-semibold text-gray-700">Assets for Borrowing</h2>
        <p className="text-3xl font-bold text-gray-900 mt-2">{assetsForBorrowing}</p>
      </div>

      {/* Stock Prize Card */}
      <div className="bg-white border border-gray-200 rounded-lg shadow-md p-6 m-4 w-full sm:w-1/2 md:w-1/3 lg:w-1/4">
        <h2 className="text-xl font-semibold text-gray-700">Stock Prize</h2>
        <p className="text-3xl font-bold text-gray-900 mt-2">{prize}</p>
      </div>

      {/* Add Asset Button for Demonstration */}
      <button onClick={() => handleAddAsset({ assetID: totalAssets + 1, cost: 100, isBorrowable: true })}>
        Add Asset
      </button>
    </div>
  );
};

export default InfoCards;
