import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faTrash, faEdit } from '@fortawesome/free-solid-svg-icons';

const AssetTable = ({ assets, onDeleteAsset, onEditAsset }) => {
  const [activeBorrowId, setActiveBorrowId] = useState(null);

  const handleBorrowClick = (assetID) => {
    setActiveBorrowId(assetID);
    setTimeout(() => setActiveBorrowId(null), 300); // Light up effect duration
  };

  return (
    <div className="relative p-4 w-full bg-white border border-gray-200 rounded-lg shadow-md">
      <table className="table-auto w-full border-collapse">
        <thead>
          <tr>
            <th>ID</th>
            <th>Date Created</th>
            <th>Asset Name</th>
            <th>Details</th>
            <th>Category</th>
            <th>Location</th>
            <th>Quantity</th>
            <th>Cost</th>
            <th>Edit</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {assets.map((asset) => (
            <tr key={asset.assetID}>
              <td>{asset.assetID}</td>
              <td>{asset.createdDate}</td>
              <td>{asset.assetName}</td>
              <td>{asset.assetDetails}</td>
              <td>{asset.selectedCategory}</td>
              <td>{asset.selectedLocation}</td>
              <td>{asset.quantity}</td>
              <td>{`â‚±${asset.cost}`}</td>
              <td>
                <button
                  className="text-blue-600 flex items-center space-x-1"
                  onClick={() => onEditAsset(asset)}
                >
                  <FontAwesomeIcon icon={faEdit} />
                  <span>Edit</span>
                </button>
              </td>
              <td className="flex items-center space-x-2">
                <button
                  className={`w-8 h-8 rounded-full flex items-center justify-center ${
                    activeBorrowId === asset.assetID ? 'bg-yellow-300' : 'bg-gray-200'
                  } border border-gray-400`}
                  onClick={() => handleBorrowClick(asset.assetID)}
                >
                  <span className="text-black">Borrow</span>
                </button>
                <button
                  className="text-red-600 flex items-center space-x-1"
                  onClick={() => onDeleteAsset(asset.assetID)}
                >
                  <FontAwesomeIcon icon={faTrash} />
                  <span>Delete</span>
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AssetTable;
