import React, { useState, useCallback } from "react";

// Modal component
const Modal = ({ isOpen, onClose, onSave }) => {
	const [categoryName, setCategoryName] = useState("");

	const handleSave = useCallback(() => {
		if (categoryName.trim()) {
			onSave(categoryName.trim());
			setCategoryName("");
			onClose();
		}
	}, [categoryName, onSave, onClose]);

	if (!isOpen) return null;

	return (
		<div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
			<div className="bg-white p-6 rounded-lg w-[400px] shadow-lg">
				<h2 className="text-lg font-semibold mb-4">Add Category</h2>
				<input
					type="text"
					placeholder="Category Name"
					value={categoryName}
					onChange={(e) => setCategoryName(e.target.value)}
					className="border p-2 rounded mb-4 w-full"
				/>
				<div className="flex justify-end gap-4">
					<button
						className="bg-gray-300 px-4 py-2 rounded-lg"
						onClick={onClose}
					>
						Cancel
					</button>
					<button
						className="bg-blue-500 px-4 py-2 text-white rounded-lg"
						onClick={handleSave}
					>
						Add Category
					</button>
				</div>
			</div>
		</div>
	);
};

// AddCategory component
const AssetCategory = () => {
	const [isModalOpen, setIsModalOpen] = useState(false);
	const [categories, setCategories] = useState([]);

	const handleOpenModal = useCallback(() => {
		setIsModalOpen(true);
	}, []);

	const handleCloseModal = useCallback(() => {
		setIsModalOpen(false);
	}, []);

	const handleSaveCategory = useCallback((categoryName) => {
		setCategories((prevCategories) => [...prevCategories, categoryName]);
	}, []);

	const handleDeleteCategory = useCallback((categoryToDelete) => {
		setCategories((prevCategories) =>
			prevCategories.filter((category) => category !== categoryToDelete)
		);
	}, []);

	return (
		<div>
			<button
				className="flex w-[112px] h-[41px] p-4 justify-center items-center gap-4 flex-shrink-0 rounded-lg bg-[#0A3B83] text-[#FFF] font-roboto text-[10px] font-semibold border border-[#000000] border-box"
				onClick={handleOpenModal}
			>
				Asset Category
			</button>

			<Modal
				isOpen={isModalOpen}
				onClose={handleCloseModal}
				onSave={handleSaveCategory}
			/>

			{/* Display the list of categories */}
			<ul className="mt-4">
				{categories.map((category, index) => (
					<li
						key={index}
						className="flex items-center justify-between p-2 border-b"
					>
						<span>{category}</span>
						<button
							className="bg-red-500 text-white px-2 py-1 rounded"
							onClick={() => handleDeleteCategory(category)}
						>
							Delete
						</button>
					</li>
				))}
			</ul>
		</div>
	);
};

export default AssetCategory;
