import React, { useState, useCallback, useEffect } from 'react';
import Button from './button';
import Modal from './modal';

const AddAsset = ({
  onAddAsset,
  assetList,
  categories = [],
  locations = [],
  selectedAsset,
  onEditAsset,
}) => {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [assetID, setAssetID] = useState(1);
  const [assetToEdit, setAssetToEdit] = useState(null);

  useEffect(() => {
    if (assetList && assetList.length > 0) {
      const maxAssetID = Math.max(...assetList.map(asset => asset.assetID), 0);
      setAssetID(maxAssetID + 1);
    }
  }, [assetList]);

  const openModal = useCallback(() => {
    setIsModalOpen(true);
  }, []);

  const closeModal = useCallback(() => {
    setIsModalOpen(false);
    setAssetToEdit(null);
  }, []);

  const handleAddAsset = useCallback(
    (newAsset) => {
      if (assetToEdit) {
        onEditAsset(newAsset);
      } else {
        onAddAsset(newAsset);
        setAssetID((prevID) => prevID + 1);
      }
      closeModal();
    },
    [onAddAsset, onEditAsset, assetToEdit, closeModal]
  );

  const handleEditAsset = useCallback(() => {
    setAssetToEdit(selectedAsset);
    openModal();
  }, [selectedAsset, openModal]);

  return (
    <div className="flex justify-between mb-4">
      <Button className="bg-blue-500 text-white" onClick={openModal}>
        Add Asset
      </Button>
      <Button
        className={`bg-yellow-500 text-white ${
          !selectedAsset ? "opacity-50 cursor-not-allowed" : ""
        }`}
        onClick={handleEditAsset}
        disabled={!selectedAsset}
      >
        Edit Asset
      </Button>

      <Modal
        isOpen={isModalOpen}
        onClose={closeModal}
        assetID={assetID}
        categories={categories}  // Pass categories to Modal
        locations={locations}    // Pass locations to Modal
        onAddAsset={handleAddAsset}
        assetToEdit={assetToEdit}
      />
    </div>
  );
};

export default AddAsset;
