import React, { useState } from "react";
import "./assetlist.css";
import AssetSearchbar from "./components/assetsearchbar";
import AssetTable from "./components/assettable";
import InfoCards from "./components/infocards";

const AssetList = () => {
  const [assets, setAssets] = useState([]);

  const handleAddAsset = (newAsset) => {
    setAssets((prevAssets) => [...prevAssets, newAsset]);
  };

  const handleDeleteAsset = (assetID) => {
    setAssets((prevAssets) => prevAssets.filter(asset => asset.assetID !== assetID));
  };

  const totalAssets = assets.length;
  const totalCost = assets.reduce((sum, asset) => sum + asset.cost, 0);
  const assetsForBorrowing = assets.filter(asset => asset.isBorrowable).length;
  const prize = assets.reduce((sum, asset) => sum + (asset.prize || 0), 0); // Example calculation for prize

  return (
    <div className="asset-list-container">
      <InfoCards
        totalAssets={totalAssets}
        totalCost={totalCost}
        assetsForBorrowing={assetsForBorrowing}
        prize={prize}
      />
      <AssetSearchbar />
      <AssetTable
        assets={assets}
        onAddAsset={handleAddAsset}
        onDeleteAsset={handleDeleteAsset}
      />
    </div>
  );
};

export default AssetList;
