import React, { useState, useCallback } from "react";
import AddCategory from "./AddCategory";
import AssetLocation from "./assetlocation";

const Modal = ({
	isOpen,
	onClose,
	assetID,
	categories,
	selectedCategory,
	setSelectedCategory,
	selectedSubCategory,
	setSelectedSubCategory,
	assetName,
	setAssetName,
	assetDetails,
	setAssetDetails,
	quantity,
	setQuantity,
	cost,
	setCost,
	locations, // New prop for locations
	selectedLocation, // New prop for selected location
	setSelectedLocation, // New prop for setting selected location
}) => {
	if (!isOpen) return null;

	const handleAddAsset = () => {
		setAssetName("");
		setAssetDetails("");
		setQuantity(1);
		setCost("");
		setSelectedCategory("");
		setSelectedSubCategory("");
		setSelectedLocation(""); // Reset location
		onClose();
	};

	return (
		<div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
			<div className="bg-white p-6 rounded-lg w-[400px] shadow-lg">
				<h2 className="text-lg font-semibold mb-4">Add Asset</h2>

				<InputField label="Asset ID" value={assetID} readOnly />
				<InputField
					label="Asset Name"
					value={assetName}
					onChange={(e) => setAssetName(e.target.value)}
				/>
				<InputField
					label="Asset Details"
					value={assetDetails}
					onChange={(e) => setAssetDetails(e.target.value)}
				/>
				<SelectField
					label="Category"
					value={selectedCategory}
					onChange={(e) => setSelectedCategory(e.target.value)}
					options={categories.map((category) => category.category)}
				/>
				<SelectField
					label="Subcategory"
					value={selectedSubCategory}
					onChange={(e) => setSelectedSubCategory(e.target.value)}
					options={
						selectedCategory
							? categories.find((cat) => cat.category === selectedCategory)
									?.subCategory
								? [
										categories.find((cat) => cat.category === selectedCategory)
											.subCategory,
								  ]
								: []
							: []
					}
				/>
				<SelectField
					label="Location" // New field for selecting location
					value={selectedLocation}
					onChange={(e) => setSelectedLocation(e.target.value)}
					options={locations} // Populate options with locations
				/>
				<InputField
					label="Quantity"
					type="number"
					value={quantity}
					onChange={(e) => setQuantity(Number(e.target.value))}
				/>
				<InputField
					label="Cost"
					value={cost}
					onChange={(e) => setCost(e.target.value.replace(/[^0-9]/g, ""))}
					prefix="₱"
				/>

				<div className="flex justify-end gap-4 mt-4">
					<Button className="bg-gray-300" onClick={onClose}>
						Cancel
					</Button>
					<Button className="bg-green-500 text-white" onClick={handleAddAsset}>
						Add Asset
					</Button>
				</div>
			</div>
		</div>
	);
};

const InputField = ({
	label,
	value,
	onChange,
	type = "text",
	readOnly = false,
	prefix,
}) => (
	<div className="mb-4 relative">
		<label className="block text-sm font-medium mb-2">{label}</label>
		<input
			type={type}
			value={value}
			onChange={onChange}
			readOnly={readOnly}
			className={`border p-2 rounded w-full ${prefix ? "pl-8" : ""}`}
			placeholder={prefix ? "" : label}
		/>
		{prefix && (
			<span className="absolute left-2 top-1/2 transform -translate-y-2/2 text-black-500">
				{prefix}
			</span>
		)}
	</div>
);

const SelectField = ({ label, value, onChange, options }) => (
	<div className="mb-4">
		<label className="block text-sm font-medium mb-2">{label}</label>
		<select
			value={value}
			onChange={onChange}
			className="border p-2 rounded w-full"
		>
			<option value="">Select {label}</option>
			{options.length > 0 ? (
				options.map((option, index) => (
					<option key={index} value={option}>
						{option}
					</option>
				))
			) : (
				<option value="">No {label.toLowerCase()} available</option>
			)}
		</select>
	</div>
);

const Button = ({ className, onClick, children }) => (
	<button className={`px-4 py-2 rounded-lg ${className}`} onClick={onClick}>
		{children}
	</button>
);

const AddAsset = () => {
	const [isModalOpen, setIsModalOpen] = useState(false);
	const [assetID, setAssetID] = useState(1); // Start with ID 1
	const [assetName, setAssetName] = useState("");
	const [assetDetails, setAssetDetails] = useState("");
	const [quantity, setQuantity] = useState(1);
	const [cost, setCost] = useState("");
	const [selectedCategory, setSelectedCategory] = useState("");
	const [selectedSubCategory, setSelectedSubCategory] = useState("");
	const [categories, setCategories] = useState([]);
	const [locations, setLocations] = useState([]); // New state for locations
	const [selectedLocation, setSelectedLocation] = useState(""); // New state for selected location

	const handleOpenModal = useCallback(() => {
		setIsModalOpen(true);
	}, []);

	const handleCloseModal = useCallback(() => {
		setIsModalOpen(false);
	}, []);

	const handleAddCategory = useCallback((newCategoryData) => {
		setCategories((prevCategories) => [...prevCategories, newCategoryData]);
	}, []);

	const handleAddLocation = useCallback((newLocation) => {
		setLocations((prevLocations) => [...prevLocations, newLocation]);
	}, []);

	return (
		<div>
			<Button
				className="bg-[#68C231] text-[#343B3F] text-[10px] font-semibold"
				onClick={handleOpenModal}
			>
				Add Asset
			</Button>
			<Modal
				isOpen={isModalOpen}
				onClose={handleCloseModal}
				assetID={assetID}
				categories={categories}
				selectedCategory={selectedCategory}
				setSelectedCategory={setSelectedCategory}
				selectedSubCategory={selectedSubCategory}
				setSelectedSubCategory={setSelectedSubCategory}
				assetName={assetName}
				setAssetName={setAssetName}
				assetDetails={assetDetails}
				setAssetDetails={setAssetDetails}
				quantity={quantity}
				setQuantity={setQuantity}
				cost={cost}
				setCost={setCost}
				locations={locations} // Pass locations to the modal
				selectedLocation={selectedLocation} // Pass selected location to the modal
				setSelectedLocation={setSelectedLocation} // Pass setSelectedLocation to the modal
			/>
			<AddCategory onSaveCategory={handleAddCategory} />
			<AssetLocation onSaveLocation={handleAddLocation} /> {/* Add AssetLocation */}
		</div>
	);
};

export default AddAsset;
