import React, { useRef } from 'react';
import { useReactToPrint } from 'react-to-print';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faPrint } from "@fortawesome/free-solid-svg-icons";
import moment from 'moment';

const PrintAssetsPDF = ({ assets }) => {
  const componentRef = useRef();

  const handlePrint = useReactToPrint({
    content: () => componentRef.current,
    documentTitle: 'Asset_Inventory',
  });

  return (
    <div>
      <button
        onClick={handlePrint}
        className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded flex items-center"
      >
        <FontAwesomeIcon icon={faPrint} className="mr-2" />
        Print Asset Inventory
      </button>

      <div style={{ display: 'none' }}>
        <PrintableAssetList ref={componentRef} assets={assets} />
      </div>
    </div>
  );
};

const PrintableAssetList = React.forwardRef((props, ref) => {
  const { assets } = props;

  return (
    <div ref={ref} className="p-8">
      <h1 className="text-3xl font-bold mb-6 text-center">Asset Inventory</h1>
      {assets.map((asset) => (
        <div key={asset.asset_id} className="mb-8 border-b pb-4">
          <h2 className="text-2xl font-semibold mb-2">{asset.assetName}</h2>
          <div className="grid grid-cols-2 gap-4">
            <div>
              <p><strong>ID:</strong> {asset.asset_id}</p>
              <p><strong>Date Created:</strong> {moment(asset.createdDate).format('MM/DD/YYYY')}</p>
              <p><strong>Quantity:</strong> {asset.quantity}</p>
              <p><strong>Status:</strong> {asset.is_active ? 'Active' : 'Inactive'}</p>
            </div>
            <div>
              <p><strong>Category:</strong> {asset.category}</p>
              <p><strong>Location:</strong> {asset.location}</p>
              <p><strong>Cost:</strong> ₱{asset.cost}</p>
              <p><strong>Type:</strong> {asset.type}</p>
            </div>
          </div>
          <p><strong>Details:</strong> {asset.assetDetails}</p>
          {asset.image && (
            <img src={asset.image} alt={asset.assetName} className="mt-4 max-w-xs mx-auto" />
          )}
        </div>
      ))}
    </div>
  );
});

export default PrintAssetsPDF;
