import React, { useState, useCallback } from 'react';

// Modal component
const Modal = ({ isOpen, onClose, onSave }) => {
  const [locationName, setLocationName] = useState('');

  const handleSave = useCallback(() => {
    if (locationName.trim()) {
      onSave(locationName.trim());
      setLocationName('');
      onClose();
    }
  }, [locationName, onSave, onClose]);

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 flex items-center justify-center z-50 bg-black bg-opacity-50">
      <div className="bg-white p-6 rounded-lg w-[400px] shadow-lg">
        <h2 className="text-lg font-semibold mb-4">Add Location</h2>
        <input
          type="text"
          placeholder="Location Name"
          value={locationName}
          onChange={(e) => setLocationName(e.target.value)}
          className="border p-2 rounded mb-4 w-full"
        />
        <div className="flex justify-end gap-4">
          <button
            className="bg-gray-300 px-4 py-2 rounded-lg"
            onClick={onClose}
          >
            Cancel
          </button>
          <button
            className="bg-blue-500 px-4 py-2 text-white rounded-lg"
            onClick={handleSave}
          >
            Add Location
          </button>
        </div>
      </div>
    </div>
  );
};

// AssetLocation component
const AssetLocation = ({ onSaveLocation }) => {
  const [isModalOpen, setIsModalOpen] = useState(false);

  const handleOpenModal = useCallback(() => {
    setIsModalOpen(true);
  }, []);

  const handleCloseModal = useCallback(() => {
    setIsModalOpen(false);
  }, []);

  return (
    <div>
      <button
        className="flex w-[112px] h-[41px] p-4 justify-center items-center gap-4 flex-shrink-0 rounded-lg bg-[#FF4B4B] text-[#343B3F] font-roboto text-[10px] font-semibold leading-normal cursor-pointer text-center"
        onClick={handleOpenModal}
      >
        Asset Location
      </button>

      <Modal
        isOpen={isModalOpen}
        onClose={handleCloseModal}
        onSave={onSaveLocation}
      />
    </div>
  );
};

export default AssetLocation;
