import React, { useState } from 'react';

const UserInfo = () => {
  const [editMode, setEditMode] = useState(false);
  const [name, setName] = useState('Vince');
  const [email, setEmail] = useState('vince@example.com');
  const [phone, setPhone] = useState('123-456-7890');

  const handleEditClick = () => {
    setEditMode(!editMode);
  };

  const handleSaveClick = () => {
    setEditMode(false);
    // Add logic to save the updated data, e.g., API call
  };

  return (
    <div className="flex flex-col items-start justify-start p-4 bg-white rounded-lg shadow-md">
      {editMode ? (
        <>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="mb-2 p-2 border border-gray-300 rounded-md w-full"
          />
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="mb-2 p-2 border border-gray-300 rounded-md w-full"
          />
          <input
            type="tel"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            className="mb-2 p-2 border border-gray-300 rounded-md w-full"
          />
          <button
            onClick={handleSaveClick}
            className="py-2 px-4 bg-green-500 text-white rounded-md"
          >
            Save
          </button>
        </>
      ) : (
        <>
          <div className="mb-2 text-xl font-bold">{name}</div>
          <div className="mb-2 text-gray-600">{email}</div>
          <div className="mb-4 text-gray-600">{phone}</div>
          <button
            onClick={handleEditClick}
            className="py-2 px-4 bg-blue-500 text-white rounded-md"
          >
            Edit Profile
          </button>
        </>
      )}
    </div>
  );
};

export default UserInfo;
