import React, { useState } from "react";
import { FaSearch } from "react-icons/fa"; // Importing a search icon

const AssetButtons = () => {
	const [isPopupVisible, setPopupVisible] = useState(false); // State to control popup visibility

	const handleAddAssetClick = () => {
		setPopupVisible(true); // Show popup
	};

	const handleClosePopup = () => {
		setPopupVisible(false); // Hide popup
	};

	return (
		<div>
			<button
				className="flex w-[112px] h-[41px] p-4 justify-center items-center gap-4 flex-shrink-0 rounded-lg bg-[#68C231] text-[#343B3F] font-roboto text-[10px] font-semibold"
				onClick={handleAddAssetClick}
			>
				Add Asset
			</button>

			{/* Popup */}
			{isPopupVisible && (
				<div className="fixed inset-0 flex items-center justify-center z-50 bg-gray-800 bg-opacity-50">
					<div className="bg-white p-6 rounded-lg shadow-lg max-w-sm w-full">
						<h2 className="text-lg font-bold mb-4">Add Asset Details</h2>
						<form>
							<label className="block mb-2">
								Asset Name:
								<input type="text" className="w-full p-2 border border-gray-300 rounded" />
							</label>
							<label className="block mb-4">
								Asset Description:
								<textarea className="w-full p-2 border border-gray-300 rounded" rows="4" />
							</label>
							<button
								type="button"
								className="bg-[#68C231] text-white py-2 px-4 rounded mr-2"
								onClick={() => {
									// Handle save logic here
									handleClosePopup();
								}}
							>
								Save
							</button>
							<button
								type="button"
								className="bg-gray-500 text-white py-2 px-4 rounded"
								onClick={handleClosePopup}
							>
								Cancel
							</button>
						</form>
					</div>
				</div>
			)}

			<button className="flex w-[112px] h-[41px] p-4 justify-center items-center gap-4 flex-shrink-0 rounded-lg bg-[#0A3B83] text-[#FFF] font-roboto text-[10px] font-semibold border border-[#000000] border-box">
				Add Category
			</button>

			<button className="flex w-[112px] h-[41px] p-4 justify-center items-center gap-4 flex-shrink-0 rounded-lg bg-[#FF4B4B] text-[#343B3F] font-roboto text-[10px] font-semibold leading-normal cursor-pointer text-center">
				Remove Asset
			</button>

			<div className="w-[335px] h-[44px] flex-shrink-0 rounded-md border border-[#9A93B3] bg-[#FEFEFE] flex items-center px-2.5">
				<FaSearch className="w-[24.484px] h-[22px] mr-2 text-[#ccc]" />
				<input
					type="text"
					placeholder="Search for Assets"
					className="text-[#787486] font-inter text-[14px] font-normal leading-normal border-none outline-none w-full"
				/>
			</div>
		</div>
	);
};

export default AssetButtons;
