import React, { useState, useCallback } from "react";
import AssetCategory from "./AssetCategory"; // Import the AssetCategory component

const AddAsset = () => {
	const [quantity, setQuantity] = useState(1);
	const [cost, setCost] = useState("");
	const [selectedCategory, setSelectedCategory] = useState("");
	const [categories, setCategories] = useState([]);
	const [isCategoryModalOpen, setIsCategoryModalOpen] = useState(false);

	// Handler for saving new category from AssetCategory component
	const handleSaveCategory = useCallback((categoryName) => {
		setCategories((prevCategories) => [...prevCategories, categoryName]);
		setSelectedCategory(categoryName); // Set the newly added category as selected
	}, []);

	// Handler for deleting a category
	const handleDeleteCategory = useCallback((categoryToDelete) => {
		setCategories((prevCategories) =>
			prevCategories.filter((category) => category !== categoryToDelete)
		);
		if (selectedCategory === categoryToDelete) {
			setSelectedCategory(""); // Clear the selected category if it was deleted
		}
	}, [selectedCategory]);

	// Open category modal
	const openCategoryModal = () => setIsCategoryModalOpen(true);
	const closeCategoryModal = () => setIsCategoryModalOpen(false);

	// Handle form submission for adding asset
	const handleAddAsset = () => {
		// Perform actions to save asset
		console.log({
			quantity,
			cost,
			selectedCategory,
		});
	};

	return (
		<div>
			<div className="p-6 rounded-lg w-[400px] shadow-lg">
				<h2 className="text-lg font-semibold mb-4">Add Asset</h2>
				<input
					type="number"
					placeholder="Quantity"
					value={quantity}
					onChange={(e) => setQuantity(Number(e.target.value))}
					className="border p-2 rounded mb-4 w-full"
				/>
				<input
					type="text"
					placeholder="Cost (e.g., 1000)"
					value={cost}
					onChange={(e) => setCost(e.target.value)}
					className="border p-2 rounded mb-4 w-full"
				/>
				<select
					value={selectedCategory}
					onChange={(e) => setSelectedCategory(e.target.value)}
					className="border p-2 rounded mb-4 w-full"
				>
					<option value="">Select a category</option>
					{categories.map((category, index) => (
						<option key={index} value={category}>
							{category}
						</option>
					))}
				</select>
				<button
					className="bg-green-500 px-4 py-2 text-white rounded-lg"
					onClick={handleAddAsset}
				>
					Add Asset
				</button>
				<button
					className="bg-blue-500 px-4 py-2 text-white rounded-lg mt-4"
					onClick={openCategoryModal}
				>
					Manage Categories
				</button>
			</div>

			{/* Category Modal */}
			<AssetCategory
				isModalOpen={isCategoryModalOpen}
				onClose={closeCategoryModal}
				onSaveCategory={handleSaveCategory}
				categories={categories}
				onDeleteCategory={handleDeleteCategory}
			/>
		</div>
	);
};

export default AddAsset;
