import { useState } from "react";
import PropTypes from "prop-types";

const UserInfo = ({ className = "" }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [name, setName] = useState("Vince Albert Juson");
  const [email, setEmail] = useState("vincejuson@ust.edu.ph");
  const [contact, setContact] = useState("09951778731");

  const handleEditClick = () => setIsEditing(true);
  const handleSaveClick = () => {
    setIsEditing(false);
    // Logic to save updated information
  };

  return (
    <div className={`bg-white p-4 rounded-lg shadow-md ${className}`}>
      <div className="text-center mb-4">
        {isEditing ? (
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            className="text-lg font-bold border rounded p-2"
          />
        ) : (
          <h3 className="text-2xl font-bold">{name}</h3>
        )}
        <p className="text-gray-600 mt-1">Student</p>
      </div>
      <div className="mb-4">
        <div className="text-left">
          <p className="font-bold">Email</p>
          {isEditing ? (
            <input
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="text-lg border rounded p-2 w-full"
            />
          ) : (
            <p>{email}</p>
          )}
        </div>
      </div>
      <div className="mb-4">
        <div className="text-left">
          <p className="font-bold">Contact</p>
          {isEditing ? (
            <input
              type="text"
              value={contact}
              onChange={(e) => setContact(e.target.value)}
              className="text-lg border rounded p-2 w-full"
            />
          ) : (
            <p>{contact}</p>
          )}
        </div>
      </div>
      <div className="text-center mt-4">
        <button
          className="bg-gray-800 text-white py-2 px-4 rounded"
          onClick={isEditing ? handleSaveClick : handleEditClick}
        >
          {isEditing ? "Save" : "Edit"}
        </button>
      </div>
    </div>
  );
};

UserInfo.propTypes = {
  className: PropTypes.string,
};

export default UserInfo;
