import React, { useState, useEffect } from 'react';
import InputField from './inputfield';
import SelectField from './selectfield';
import Button from './button';

// Format date helper function
const formatDate = (date) => {
  const options = { year: "numeric", month: "2-digit", day: "2-digit" };
  return new Intl.DateTimeFormat("en-US", options).format(new Date(date));
};

// CSS for shaking animation
const shakeStyle = `
@keyframes shake {
  0% { transform: translateX(0); }
  25% { transform: translateX(-5px); }
  50% { transform: translateX(5px); }
  75% { transform: translateX(-5px); }
  100% { transform: translateX(0); }
}

.shake {
  animation: shake 0.5s;
}
`;

// Add style to the document head
const addShakeStyle = () => {
  const styleElement = document.createElement("style");
  styleElement.innerHTML = shakeStyle;
  document.head.appendChild(styleElement);
};

const Modal = ({
  isOpen,
  onClose,
  assetID,
  categories = [],
  locations = [],
  onAddAsset,
  assetToEdit,
}) => {
  const [assetName, setAssetName] = useState("");
  const [assetDetails, setAssetDetails] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [cost, setCost] = useState("");
  const [selectedCategory, setSelectedCategory] = useState("");
  const [selectedLocation, setSelectedLocation] = useState
  const [acquisitionDate, setAcquisitionDate] = useState("");
  const [image, setImage] = useState(null);
  const [shakeFields, setShakeFields] = useState({});

  useEffect(() => {
    addShakeStyle();
  }, []);

  useEffect(() => {
    if (assetToEdit) {
      setAssetName(assetToEdit.assetName || "");
      setAssetDetails(assetToEdit.assetDetails || "");
      setQuantity(assetToEdit.quantity || 1);
      setCost(assetToEdit.cost || "");
      setSelectedCategory(assetToEdit.category || "");
      setSelectedLocation(assetToEdit.location || "");
      setAcquisitionDate(formatDate(assetToEdit.acquisitionDate) || "");
      setImage(assetToEdit.image || null);
    }
  }, [assetToEdit]);

  const validateFields = () => {
    const fieldsToShake = {};
    if (!assetName) fieldsToShake.assetName = true;
    if (!selectedCategory) fieldsToShake.selectedCategory = true;
    if (!selectedLocation) fieldsToShake.selectedLocation = true;
    if (!acquisitionDate) fieldsToShake.acquisitionDate = true;

    setShakeFields(fieldsToShake);
    return Object.keys(fieldsToShake).length === 0;
  };

  const handleImageUpload = (event) => {
    const file = event.target.files[0];
    if (file) {
      setImage(URL.createObjectURL(file));
    }
  };

  const handleSubmit = () => {
    if (!validateFields()) {
      return;
    }

    const assetData = {
      assetID,
      assetName,
      assetDetails,
      quantity,
      cost,
      category: selectedCategory,
      location: selectedLocation,
      acquisitionDate,
      image,
    };

    onAddAsset(assetData);
    onClose();
  };

  return (
    isOpen && (
      <div className="fixed inset-0 flex items-center justify-center z-50">
        <div className="bg-white p-8 rounded-lg shadow-lg w-full max-w-lg">
          <h2 className="text-2xl font-bold mb-4">
            {assetToEdit ? "Edit Asset" : "Add New Asset"}
          </h2>
          <InputField
            label="Asset Name"
            value={assetName}
            onChange={(e) => setAssetName(e.target.value)}
            shake={shakeFields.assetName}
          />
          <InputField
            label="Asset Details"
            value={assetDetails}
            onChange={(e) => setAssetDetails(e.target.value)}
          />
          <InputField
            label="Quantity"
            type="number"
            value={quantity}
            onChange={(e) => setQuantity(Number(e.target.value))}
          />
          <InputField
            label="Cost"
            type="number"
            value={cost}
            onChange={(e) => setCost(e.target.value)}
          />
          <SelectField
            label="Category"
            value={selectedCategory}
            onChange={(e) => setSelectedCategory(e.target.value)}
            options={categories}
            shake={shakeFields.selectedCategory}
          />
          <SelectField
            label="Location"
            value={selectedLocation}
            onChange={(e) => setSelectedLocation(e.target.value)}
            options={locations}
            shake={shakeFields.selectedLocation}
          />
          <InputField
            label="Acquisition Date"
            type="date"
            value={acquisitionDate}
            onChange={(e) => setAcquisitionDate(e.target.value)}
            shake={shakeFields.acquisitionDate}
          />
          <div className="mb-4">
            <label className="block text-sm font-medium mb-2">
              Upload Image
            </label>
            <input
              type="file"
              accept="image/*"
              onChange={handleImageUpload}
              className="w-full"
            />
            {image && (
              <img
                src={image}
                alt="Asset"
                className="mt-2 w-full h-48 object-cover rounded"
              />
            )}
          </div>
          <div className="flex justify-end">
            <Button onClick={onClose} className="mr-2">
              Cancel
            </Button>
            <Button onClick={handleSubmit} primary>
              {assetToEdit ? "Update Asset" : "Add Asset"}
            </Button>
          </div>
        </div>
      </div>
    )
  );
};

export default Modal;
