import { useState } from "react";
import PropTypes from "prop-types";

const CardInfo = ({ className = "", onSave }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [buttonText, setButtonText] = useState("Edit Profile");

  const handleEditClick = () => {
    setIsEditing(!isEditing);
  };

  const handleSaveClick = () => {
    setIsEditing(false);
    onSave(buttonText); // Call the onSave function passed as a prop to update the parent component
  };

  return (
    <button
      onClick={isEditing ? handleSaveClick : handleEditClick}
      className={`cursor-pointer [border:none] py-[5px] px-[15px] bg-[#dbdbdb] rounded-[8px] flex flex-row items-center justify-center gap-[16px] z-[2] ${className}`}
    >
      {isEditing ? (
        <input
          type="text"
          value={buttonText}
          onChange={(e) => setButtonText(e.target.value)}
          className="text-[20px] font-[Roboto] text-[#343b3f] text-center border border-gray-300 rounded-lg px-2 py-1 w-full mq450:text-[16px]"
        />
      ) : (
        <div className="relative text-[20px] font-[Roboto] text-[#343b3f] text-left inline-block min-w-[96px] mq450:text-[16px]">
          {buttonText}
        </div>
      )}
    </button>
  );
};

CardInfo.propTypes = {
  className: PropTypes.string,
  onSave: PropTypes.func.isRequired, // Ensure onSave is passed as a prop
};

export default CardInfo;
