import React, { useState, useCallback } from 'react';
import AddAsset from './AddAsset';
import AssetCategory from './AssetCategory';

const AssetList = () => {
  const [categories, setCategories] = useState([]);
  const [assets, setAssets] = useState([]);

  const handleAddCategory = useCallback(({ category, subCategory }) => {
    setCategories((prev) => {
      const existingCategory = prev.find(cat => cat.category === category);
      if (existingCategory) {
        // Update existing category with new subcategory
        return prev.map(cat =>
          cat.category === category
            ? { ...cat, subCategory: [...(cat.subCategory || []), subCategory] }
            : cat
        );
      }
      // Add new category with subcategory
      return [...prev, { category, subCategory: subCategory ? [subCategory] : [] }];
    });
  }, []);

  const handleAddAsset = (newAsset) => {
    setAssets((prevAssets) => [...prevAssets, newAsset]);
  };

  return (
    <div>
      <AssetCategory onSaveCategory={handleAddCategory} />
      <AddAsset
        assetID={assets.length + 1}
        onAddAsset={handleAddAsset}
        categories={categories}
        locations={["Location 1", "Location 2", "Location 3"]} // Example locations
      />
      {/* Render assets and other UI components here */}
    </div>
  );
};

export default AssetList;
